script.body:
var reqId = hibSession.createQuery('select max(requirementId) from DistributionType').uniqueResult();
var reference = 'MAX_DAYS_RANGE(' + days + ')';
if (workdays) reference = 'MAX_WORKDAYS(' + days + ')';
var type = hibSession.createQuery('from DistributionType where reference = :reference').setParameter('reference', reference).uniqueResult();
var abbv = 'Span Max ' + days + ' Days';
var label = 'At Most ' + days + ' Consecutive Days';
if (workdays) {
  abbv = 'Span Max ' + days + ' Work Days';
  label = 'At Most ' + days + ' Consecutive Work Days';
}
var desc = 'Given classes must be taught within ' + days + ' consecutive ' + (workdays ? 'work days' : 'days') + '.';
desc = desc + ' This means that between the first and last meeting of all classes in the constraint, ' +
  'there cannot be more than ' + days + ' consecutive ' + (workdays ? 'work days' : 'days') + ', including the first and last day.';
if (workdays)
  desc = desc + ' Weekends, holidays, and breaks are ignored.';
if (type == null) {
  type = new org.unitime.timetable.model.DistributionType();
  type.setReference(reference);
  log.info('Creating ' + label + ' constraint...');
} else {
  log.warn('Constraint ' + label + ' already exists.');
}
type.setLabel(label);
type.setSequencingRequired(false);
type.setRequirementId(++reqId);
type.setAllowedPref('210R');
type.setDescr(desc);
type.setAbbreviation(abbv);
type.setInstructorPref(false);
type.setExamPref(false);
type.setVisible(true);
hibSession.saveOrUpdate(type);
log.info('All done.');
